definition.script:
pipeline {
    agent any

    tools {
        jdk 'java 17'
        maven 'maven'
    }

    stages {
        stage('Git Checkout') {
            steps {
                git branch: 'main', url: 'https://github.com/divy1436/Boardgame.git'
            }
        }
        stage('Hello') {
            steps {
                echo 'Hello World'
            }
        }
        stage('Compile') {
            steps {
                sh 'mvn compile'
            }
        }
        stage('testing') {
            steps {
                sh 'mvn test'
            }
        }
        stage('Building The Project') {
            steps {
                sh 'mvn package'
            }
        }
    }
}
