definition.script:
pipeline {
    agent any
    tools {
        jdk 'java 17'
        maven 'maven'
    }
    parameters {
        booleanParam(
            name: 'RUN_TESTS',
            defaultValue: true,
            description: 'Enable or disable test execution'
        )
    }

    stages {
        stage('Build') {
            steps {
                sh 'mvn package'
            }
        }

        stage('Test') {
            when {
                expression { params.RUN_TESTS }
            }
            steps {
                sh 'mvn test'
            }
        }
    }
    post {
        success{
            echo "pipeline is success"
        }
        failure {
            echo "pipeline is failed" 
        }
        always {
            echo "jon completed at: ${new Date()}"
        }
    }
}
